import { Accordion, AccordionDetails, AccordionSummary, Box, CardMedia, List, ListItemButton, Typography } from '@mui/material'
import React from 'react'
import { BASE_URL } from '../api'

export default function Answer({ qnAnswers }) {
    return (
        <Box sx={{ mt: 5, width: '100%', maxWidth: 640, mx: 'auto' }}>
            {
                qnAnswers.map((item, j) => (<Accordion
                    disableGutters
                    key={j}>
                    <AccordionSummary>
                        <Typography
                            sx={{ width: '90%', flexShrink: 0 }}>
                            {item.qnInWords}
                        </Typography>
                    </AccordionSummary>
                    <AccordionDetails>
                        {item.imageName ?
                            <CardMedia
                                component="img"
                                image={BASE_URL + 'images/' + item.imageName}
                                sx={{ m: '10px auto', width: '50%' }}
                            /> : null}
                        <List>
                            {qns[qnIndex].options.map((item, idx) =>
                                <ListItemButton key={idx} onClick={() => updateAnswer(qns[qnIndex].qnId, idx)}>
                                    <div>
                                        <b>{String.fromCharCode(65 + idx) + " . "}</b> {item}
                                    </div>
                                </ListItemButton>
                            )}

                        </List>
                    </AccordionDetails>
                </Accordion>))
            }
        </Box>
    )
}
